void push_front(int val){

Node* newNode= new Node(val);

if(head==NULL){
    head=tail=newNode;
}else{
    newNode->next=head;
    head=newNode;

}

}

void push_back(int val){

Node* newNode= new Node(val);

if(head==NULL){
    head=tail=newNode;
}else{
    tail->next=newNode;
    tail=newNode;

}

}

